sys_script.script:
(function executeRule(current) {
    gs.info("RUN - Create Assignmnet group for " + current.environment_name);
    (function create_assignment_group() {
        try {
            gs.info("current assignment group:" + current.assignment_group);
            if (current.assignment_group != '') {
                gs.info("This environment already has an assignment group: " + current.assignment_group );
            } else {
                var group = new GlideRecord('sys_user_group');
                group.addQuery('name','=', 'AMS-' + current.environment_name);
                group.query();
                if (group.next()) {
                    gs.info("An assignment group with the name AMS-" + current.environment_name + " exists already." );
                } else {
                    var new_group = new GlideRecord('sys_user_group');
                    new_group.initialize();
                    new_group.name = 'AMS-' + current.environment_name;
                    new_group.insert();
                    current.assignment_group = new_group.sys_id;
                    current.create_assignment_group = false;
                    current.update();
                    gs.info("Created assignment group " + new_group.name + " for AMS environment: " + current.environment_name );
                }
            }

        }
        catch (ex) {
            gs.info(ex);
        }
    })();
})(current);
