sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    gs.info("RUN - Create AMS Case");
    (function create_and_link_ams_incident() {
        try {
            if (current.x_195647_ams_u_ams_case_id == "" ) {
                var impact = 'normal';
                if (current.impact == 2) {
                    impact = 'urgent';
                } else if (current.impact == 1) {
                    impact = 'critical';
                }
				var cc_addresses = String(current.x_195647_ams_cc_email_addresses).split(',');
                var request = new sn_ws.RESTMessageV2();
                request.setHttpMethod('post');
                var params = {
                  communicationBody: 'AMSTestNoOpsActionRequired - ' + current.description,
                  subject: 'AMSTestNoOpsActionRequired - ' + current.short_description,
                  categoryCode: 'other',
                  serviceCode: 'sentinel-report-incident',
                  severityCode: impact
                };
				//if (cc_addresses.length > 0) {
				//  params.ccEmailAddresses = cc_addresses;
				//}
                // why wont this work?
                //request.setRequestBody(body);
                //gs.info(request.getRequestBody());
				gs.info("RUN - set endpoint");
                request.setEndpoint('http://91b21d7e3a00801579d29e080c0eb96b7599571e:a8e4d35634a2471e7b03348567abf727231c1e44@54.167.195.83:3000/incidents');
				request.setEndpoint('http://54.167.195.83:3000/incidents');
                request.setQueryParameter('params', JSON.stringify(params));
				gs.info("RUN - execute");
                var response = request.execute();
                ams_case_id = JSON.parse(response.getBody()).caseId;
                gs.info("Created AMS Case ID: " + ams_case_id);

                current.x_195647_ams_u_ams_case_id = ams_case_id;
                current.update();
            } else {
                gs.info("Incident linked already :" + current.x_195647_ams_u_ams_case_id);
            }
        }

        catch (ex) {
            gs.info(ex);
        }
    })();
})(current, previous);
