sys_script.script:
(function executeRule(current, previous) {
    gs.info("RUN - CreateAMSCase");
    (function create_and_link_ams_incident() {
        try {
            var ams_incident = new GlideRecord('x_195647_ams_ams_incidents');
            ams_incident.addQuery('incident','=', current.sys_id);
            ams_incident.query();
            if (ams_incident.next()) {
                gs.info("Incident exists :" + ams_incident.case_id);
            } else {
                var ams_account = new GlideRecord('x_195647_ams_ams_accounts');
                ams_account.addQuery('assignment_group','=', current.assignment_group.sys_id);
                ams_account.query();
                if (ams_account.next()) {
                    gs.info("Found an AMS account for the " + current.assignment_group.name + " assignment group: " + ams_account.environment_name);
                    var impact = 'normal';
                    if (current.impact == 2) {
                        impact = 'urgent';
                    } else if (current.impact == 1) {
                        impact = 'critical';
                    }
                    var request = new sn_ws.RESTMessageV2();
                    request.setEndpoint('http://54.167.195.83:3000/incidents');
                    var credentials = { 
                        accessKeyId: String(ams_account.api_key),
                        secretAccessKey: String(ams_account.secret_key)
                    };
                    request.setQueryParameter('credentials', JSON.stringify(credentials));
                    request.setHttpMethod('post');
                    var params = {
                      communicationBody: 'AMSTestNoOpsActionRequired - ' + current.description,
                      subject: 'AMSTestNoOpsActionRequired - ' + current.short_description,
                      categoryCode: 'other',
                      serviceCode: 'sentinel-report-incident',
                      severityCode: impact
                    };
                    request.setQueryParameter('params', JSON.stringify(params));

                    //}
                    // why wont this work?
                    //request.setRequestBody(body);
                    //gs.info(request.getRequestBody());
                    var response = request.execute();
                    var ams_case_id = JSON.parse(response.getBody()).caseId;
                    gs.info("Created AMS Case ID: " + ams_case_id);

                    // insert in AMS Incidents table
                    var new_ams_incident = new GlideRecord('x_195647_ams_ams_incidents');
                    new_ams_incident.initialize();
                    new_ams_incident.case_id = ams_case_id;
                    new_ams_incident.ams_account = ams_account.sys_id;
                    new_ams_incident.incident = current.sys_id;
                    new_ams_incident.insert();
                    gs.info("Created new AMS Incident record: " + new_ams_incident.sys_id);
                } else {
                    gs.error("Assignment group is not realted to an AMS account");
                }
            }
        }
        catch (ex) {
            gs.info(ex);
        }
    })();
})(current, previous);
