sys_script.script:
//gs.info("RUN - PushIncidentAttachments "+current.value);
gs.include('AMSIncidentAPI');
(function executeRule(current, previous) {
    (function pushIncidentComments() {
        if (current.created_by == "admin") { return;}
        if (current.table_name == 'incident') {
            var ams_incident = new GlideRecord('x_195647_ams_ams_incidents');
            ams_incident.addQuery('incident','=', current.table_sys_id);
            ams_incident.query();
            if (ams_incident.next()) {
                var creds = {
                    accessKeyId: String(ams_incident.ams_account.api_key),
                    secretAccessKey: String(ams_incident.ams_account.secret_key)
                };
                var sa = new GlideSysAttachment();
                var data = sa.getContentBase64(current);

                var params = {
                    attachments: [
                        {
                            fileName: String(current.file_name), 
                            data: data
                        }
                    ]
                };

                AMSApi = new AMSIncidentAPI(creds);
                var attachmentResponse = AMSApi.addAttachmentsToSet(params);

                params = {
                    caseId: String(ams_incident.case_id),
                    communicationBody: 'New attachment.',
                    attachmentSetId: attachmentResponse.attachmentSetId
                };
                var result = AMSApi.addCaseCommunications(params);
            }
        }
    })();
})(current, previous);
