sys_script_include.script:
gs.include('AMSChangeManagementAPI');
var AMSChangeTypesIngestion = Class.create();
AMSChangeTypesIngestion.prototype = {
    initialize: function(ams_account) {
      this.ams_account = ams_account;
      var creds = {
        accessKeyId: String(ams_account.api_key),
        secretAccessKey: String(ams_account.secret_key)
      };
      this.amscm = new AMSChangeManagementAPI(creds);
      //this.catalog_item_table = 'x_195647_ams_ams_change_type_catalog';
      //this.catalog_item_table = 'x_195647_ams_ams_change_type_producer';
      this.catalog_item_table = 'sc_cat_item_producer';
      //this.category_table = 'sc_category';
      this.category_table = 'x_195647_ams_ams_change_type_categories';
      //this.variable_table = 'item_option_new';
      this.variable_table = 'x_195647_ams_ams_change_type_variables';
      //this.producer_table = 'x_195647_ams_ams_change_requests';
      this.producer_table = 'change_request';

      var sc_category = new GlideRecord('sc_category');
      sc_category.addQuery("title", "=", 'AMS Change Types');
      sc_category.query();
      if (sc_category.next()){
        this.catalog_category = sc_category;
        this.catalog = sc_category.sc_catalog;
      }
    },

    ingestChangeTypes: function () {
      // creates catalogItems in this.catalog from an array of AMS change types
      // the array is the one produced by AMSChangeManagementAPI.getChangeTypeIds
      var change_types = this.amscm.getChangeTypeIds();
      for (var i = 0; i < change_types.length; i++) {
        var item = new GlideRecord(this.catalog_item_table);
        //this does not work with sc_cat_item_producer record producer table.
        //item.addQuery("change_type_id", "=", change_types[i].ChangeTypeId);
        item.addQuery("name", "=", change_types[i].Operation +' '+ change_types[i].Item);
        item.query();
        if (item.next()) {
          item.deleteRecord();
          gs.info("Deleted item"+item.name);
        }
        item.initialize();
        item.name = change_types[i].Operation +' '+ change_types[i].Item;

        // if I use the sc_cat_item_producer table, I can't store permanently the ID
        item.change_type_id = change_types[i].ChangeTypeId;

        item.table_name = this.producer_table;
        item.sc_catalogs = this.catalog;
        item.ignore_price = true;

        //item.no_quantity = true;
        //item.billable = false;
        //item.can_cancel = true;

        // It finds or create the CT Category,subcategory
        var category = new GlideRecord(this.category_table);
        category.addQuery("title", "=", change_types[i].Category);
        category.query();
        if (!category.next()){
          category.initialize();
          category.title = change_types[i].Category;
          category.parent = this.catalog_category.sys_id;
          category.insert();
          //gs.info("NEW Category: "+category.sys_id);
        }
        var sub_category = new GlideRecord(this.category_table);
        sub_category.addQuery("title", "=", change_types[i].Subcategory);
        sub_category.addQuery("parent", "=", category.sys_id);
        sub_category.query();
        if (!sub_category.next()){
          sub_category.initialize();
          sub_category.title = change_types[i].Subcategory;
          sub_category.parent = category.sys_id;
          sub_category.insert();
          //gs.info("NEW SUB Category: "+sub_category.sys_id);
        }
        item.category = sub_category.sys_id;

        try {
          var change_type_schema = this.amscm.getChangeTypeVersion({ChangeTypeId: String(item.change_type_id)});
          if (change_type_schema) {
            var execution_input_schema = JSON.parse(change_type_schema.ExecutionInputSchema);
            var schema_properties_keys = Object.keys(execution_input_schema.properties);
            //gs.info("CT Properties " + JSON.stringify(schema_properties_keys) + " CT ID " + item.change_type_id);



            item.short_description = execution_input_schema.description;
            item.insert();
            gs.info("Insert CT " + item.sys_id + " ct id " + item.name + " category "+ item.category);

            // variables:
            // goes and gets the schema with getChangeTypeVersion
            // iterates the schema and create variables for each attribute of ExecutionInputSchema
            //gs.info("CT ID " + item.change_type_id);
            //gs.info("SCHEMA" + JSON.stringify(change_type_schema));

            //insert the change type id and version as variables in the producer
            var variable = new GlideRecord(this.variable_table);
            variable.initialize();
            variable.cat_item = item.sys_id;
            variable.name = 'ChangeTypeId';
            variable.question_text = 'ChangeTypeId';
            variable.tooltip = 'ChangeTypeId';
            variable.default_value = change_types[i].ChangeTypeId;
            variable.insert();

            variable = new GlideRecord(this.variable_table);
            variable.initialize();
            variable.cat_item = item.sys_id;
            variable.name = 'ChangeTypeVersion';
            variable.question_text = 'ChangeTypeVersion';
            variable.tooltip = 'ChangeTypeVersion';
            variable.default_value = change_type_schema.Version;
            variable.insert();

            for (var p = 0; p < schema_properties_keys.length; p++) {
              //gs.info("quiero meter variable"+schema_properties_keys[p]);
              var variable = new GlideRecord(this.variable_table);
              variable.initialize();
              variable.cat_item = item.sys_id;
              variable.name = schema_properties_keys[p];
              variable.question_text = schema_properties_keys[p];
              variable.tooltip = execution_input_schema.properties[schema_properties_keys[p]]['description'];
              if (execution_input_schema.properties[schema_properties_keys[p]].hasOwnProperty('enum')) {
                variable.default_value = execution_input_schema.properties[schema_properties_keys[p]]['enum'][0];
              }
              if (execution_input_schema.properties[schema_properties_keys[p]].hasOwnProperty('default')) {
                variable.default_value = execution_input_schema.properties[schema_properties_keys[p]]['default'];
              }
              variable.insert();
              //gs.info("Insert variable"+variable.sys_id+" name "+variable.name);
            }
            vars = '["' + schema_properties_keys.join('","') + '"]';
            item.script =  'var variables = '+ vars+';var RfcParameters = {}; RfcParameters.ChangeTypeId = String(producer.ChangeTypeId); RfcParameters.ChangeTypeVersion = String(producer.ChangeTypeVersion); ExecutionParameters = {}; for (var i = 0; i < variables.length; i++) {ExecutionParameters[variables[i]] = String(producer[variables[i]]);} RfcParameters.ExecutionParameters = JSON.stringify(ExecutionParameters); current.implementation_plan = JSON.stringify(RfcParameters);';
            item.update();
            //break;
          }
        }
        catch(err) {
          gs.info("Error processing ct "+item.change_type_id + " message "+err.message);
          //break;
          continue;
        }
      }
    },

    type: 'AMSChangeTypesIngestion'
};
